import './App.css';
import { useState } from 'react';

function App() {

  const [getList, setList] = useState([{
    stage: "Request submitted",
    city: "New york",
    code: "completed"
  },
  {
    stage: "Package received",
    city: "New york",
    code: "progress"
  },
  {
    stage: "Transit",
    city: "New york",
    code: "progress"
  }

  ]);

  return (
    <div className="container">
      {getList.map((obj, index) => {
        return (<div className="box" keys={index}>
         {obj.code == "completed" && <><div className='full-circle'></div><div className='vertical-line-blue'></div></>} 
         {obj.code == "progress" && <><div className='full-circle'></div><div className='vertical-line-blue'></div></>} 
         {obj.code == "backlog" && <><div className='full-circle'></div><div className='vertical-line-blue'></div></>} 

        </div>)
      })}


    </div>
  )
}

export default App;
